package main

import "fmt"

const input = ""

// abcd

func main() {
	fourdistinct := detectUnique(4)
	fourteendistinct := detectUnique(14)

	fmt.Printf("Found 4 characters at idx: %d string: %s\n", fourdistinct, input[fourdistinct:fourdistinct+4])
	fmt.Printf("Found 14 characters at idx: %d string %s", fourteendistinct, input[fourteendistinct:fourteendistinct+14])
}

func detectUnique(amount int) int {
	nextPossible := 0
	for {
		seen := map[byte]int{}
		if nextPossible+amount > len(input) {
			break
		}

		found := true
		for i := nextPossible; i < nextPossible+amount; i++ {
			if idx, ok := seen[input[i]]; ok {
				nextPossible = idx + 1
				found = false

				break
			} else {
				seen[input[i]] = i
			}
		}

		if found {
			fmt.Println("blerb")
			break
		}
	}

	return nextPossible
}
